import React, { Component } from 'react';
import './App.css';
import Dashboard from './compopnents/Dashboard';
import Model from './compopnents/Model';
import axios from 'axios';
import ReactModal from 'react-modal';

const customStyles = {
    content:{
        top                   : '50%',
        left                  : '50%',
        right                 : 'auto',
        bottom                : 'auto',
        marginRight           : '-50%',
        transform             : 'translate(-50%, -50%)',
        width                 : '40%',
        height                : '40%'
    }
};

ReactModal.setAppElement('#root');

class Main extends Component{
    constructor () {
        super();
        this.state = {
          showModal: false,
          arrivals: []
        };

        this.handleOpenModal = this.handleOpenModal.bind(this);
        this.handleCloseModal = this.handleCloseModal.bind(this);
      }

      handleOpenModal () {
        this.setState({ showModal: true });
      }

      handleCloseModal () {
        this.setState({ showModal: false });
      }

    subtractMin = (min) => {
        var result = new Date();
        result.setMinutes(result.getMinutes() - min);
        return Math.round(result.getTime()/1000);
    }

    ICAO = ["KDFW","KIAH","KJFK","KATL","KSFO","KLAX","KORD","KDEN","KLAS","KSEA"];

    componentDidMount () {
        // const endTime = Math.round((new Date()).getTime()/1000);
        // const startTime = this.subtractMin(10000);

        // console.log(startTime);
        // console.log(endTime);

//estArrivalAirport airport
// ICAO code

        // for(var i=0;i<this.ICAO.length;i++){
        //     const url = 'https://opensky-network.org/api/flights/arrival?airport='+this.ICAO[i]+'&begin='+startTime+'&end='+endTime+'';
        //     console.log(url);
        //     axios.get(url)
        //             .then(response => {
        //                 const updateArrivals = response.data;
        //                 this.setState({arrivals:updateArrivals});
        //             });
        // }

    }

    airportSelectedHandler = (icao) => {
        console.log('airport: '+icao);
        const startTime = this.subtractMin(10000);
        const endTime = this.subtractMin(0);
        const url = 'https://opensky-network.org/api/flights/arrival?airport='+icao+'&begin='+startTime+'&end='+endTime+'';
        axios.get(url)
                    .then(response => {
                        const updateArrivals = response.data;
                        console.log(updateArrivals);
                        this.setState({arrivals:updateArrivals});
                    });
    }

    render() {

         const airports = this.ICAO.map(airport => {
             return <Dashboard 
                title={airport}
                clicked={() => this.airportSelectedHandler(airport)}
            />;
         });

        return (

            <div>
                {airports}
                <button onClick={this.handleOpenModal}>Trigger Modal</button>
                <ReactModal 
                    style={customStyles}
                    isOpen={this.state.showModal}
                    onRequestClose={this.handleCloseModal}
                >
                    <Model/>
                    <button onClick={this.handleCloseModal}>Close Modal</button>
                </ReactModal>
            </div>

        );
      }

}

export default Main;
