import React, {useState} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import SwipeableViews from 'react-swipeable-views';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';

import axios from 'axios';

function TabContainer({ children, dir }) {
  return (
    <Typography component="div" dir={dir} style={{ padding: 8 * 3 }}>
      {children}
    </Typography>
  );
}

TabContainer.propTypes = {
  children: PropTypes.node.isRequired,
  dir: PropTypes.string.isRequired,
};

const styles = theme => ({
  root: {
    backgroundColor: theme.palette.background.paper,
    width: 770
  },
  table: {
    minWidth: 300,
  },
  formControl: {
    margin: theme.spacing.unit,
    minWidth: 120,
  },
});

const FullWidthTabs = props => {

  const [airportState, setAirportState] = useState({
    value: 0,
    min: '',
    arrivals: [],
    departures: [],
});

  const subtractMin = (min) => {
    var result = new Date();
    result.setMinutes(result.getMinutes() - min);
    return Math.round(result.getTime()/1000);
}

  const handleChangeMin = min => event => {
    setAirportState({ [min]: event.target.value });

    //console.log('airport: '+icao);
        const icao = "KSFO";
        const startTime = subtractMin(1000);
        const endTime = subtractMin(0);
        //const url = 'https://opensky-network.org/api/flights/arrival?airport='+icao+'&begin='+startTime+'&end='+endTime+'';
        const arrurl = "https://opensky-network.org/api/flights/arrival?airport=EDDF&begin=1517227200&end=1517230800";
        axios.get(arrurl)
                    .then(response => {
                        const updateArrivals = response.data;
                        console.log(updateArrivals);
                        setAirportState({arrivals:updateArrivals});
                    });

         const depurl = "https://opensky-network.org/api/flights/departure?airport=EDDF&begin=1517227200&end=1517230800";      
         axios.get(depurl)
                    .then(response => {
                        const updateDeparture = response.data;
                        console.log(updateDeparture);
                        setAirportState({departures:updateDeparture});
                    });   
  };

  const handleChange = (event, value) => {
    setAirportState({ value });
  };

  const handleChangeIndex = index => {
    setAirportState({ value: index });
  };

    const { classes, theme } = props;
console.log(departures);

    return (
      <div className={classes.root}>
        <AppBar position="static" color="default">
          <Tabs
            value={airportState.value}
            onChange={handleChange}
            indicatorColor="primary"
            textColor="primary"
            variant="fullWidth"
          >
            <Tab label="Arrival" />
            <Tab label="Departure" />
          </Tabs>
        </AppBar>
        <SwipeableViews
          axis={theme.direction === 'rtl' ? 'x-reverse' : 'x'}
          index={airportState.value}
          onChangeIndex={handleChangeIndex}
        >
          <TabContainer dir={theme.direction}>
                <Table className={classes.table}>
                    <TableHead>
                    <TableRow>
                        <TableCell align="right">ICAO number</TableCell>
                        <TableCell align="right">Arrival Time</TableCell>
                    </TableRow>
                    </TableHead>
                    <TableBody>
                     {airportState.arrivals.map(row => (
                        <TableRow key={row.icao24}>
                        <TableCell align="right" component="th" scope="row">
                            {row.icao24}
                        </TableCell>
                        <TableCell align="right">{row.lastSeen}</TableCell>
                        </TableRow>
                    ))} 
                    </TableBody>
                </Table>
          </TabContainer>
          <TabContainer dir={theme.direction}>
                 <Table className={classes.table}>
                    <TableHead>
                    <TableRow>
                        <TableCell align="right">ICAO number</TableCell>
                        <TableCell align="right">Departure Time</TableCell>
                    </TableRow>
                    </TableHead>
                    <TableBody>
                     {airportState.departures.map(row => (
                        <TableRow key={row.icao24}>
                        <TableCell align="right" component="th" scope="row">
                            {row.icao24}
                        </TableCell>
                        <TableCell align="right">{row.firstSeen}</TableCell>
                        </TableRow>
                    ))} 
                    </TableBody>
                </Table>
          </TabContainer>
        </SwipeableViews>
        <FormControl className={classes.formControl}>
          <InputLabel htmlFor="age-native-simple">Minute</InputLabel>
          <Select
            native
            value={airportState.min}
            onChange={handleChangeMin('min')}
            inputProps={{
              name: 'age',
              id: 'age-native-simple',
            }}
          >
            <option value="" />
            <option value={10}>10</option>
            <option value={20}>20</option>
            <option value={30}>30</option>
          </Select>
        </FormControl>
      </div>
    );

}

FullWidthTabs.propTypes = {
  classes: PropTypes.object.isRequired,
  theme: PropTypes.object.isRequired,
};

export default withStyles(styles, { withTheme: true })(FullWidthTabs);
